<node CREATED="1341888455975" ID="ID_876092036" MODIFIED="1341902588547" POSITION="left" TEXT="&#x578b;&#x5225;&#x8f49;&#x63db;">
<richcontent TYPE="NOTE"><html>
  <head>
    
  </head>
  <body>
    <p style="margin-bottom: 0in; font-size: 12.0pt; margin-left: 0in; margin-right: 0in; margin-top: 0in; font-family: Corbel">
      [int]$Age=1877&#160;&#160;#&#160;remember&#160;&#160;gm &amp; method
    </p>
    <p style="margin-bottom: 0in; font-size: 12.0pt; margin-left: 0in; margin-right: 0in; margin-top: 0in; font-family: Corbel">
      $Age.GetType()
    </p>
    <p style="margin-bottom: 0in; font-size: 12.0pt; margin-left: 0in; margin-right: 0in; margin-top: 0in; font-family: Corbel">
      $Age.GetType().Name&#160;&#160;
    </p>
    <p style="margin-bottom: 0in; font-size: 12.0pt; margin-left: 0in; margin-right: 0in; margin-top: 0in; font-family: Corbel">
      $Age | gm
    </p>
    <p style="margin-bottom: 0in; font-size: 12.0pt; margin-left: 0in; margin-right: 0in; margin-top: 0in; font-family: Corbel">
      $agetext=$age.ToString()
    </p>
    <p style="margin-bottom: 0in; font-size: 12.0pt; margin-left: 0in; margin-right: 0in; margin-top: 0in; font-family: Corbel">
      <font color="#ff3366">$agetext | gm |fl</font>
    </p>
    <p style="margin-bottom: 0in; font-size: 12.0pt; margin-left: 0in; margin-right: 0in; margin-top: 0in; font-family: Corbel">
      $al=$agetext.Length
    </p>
    <p style="margin-bottom: 0in; font-size: 12.0pt; margin-left: 0in; margin-right: 0in; margin-top: 0in; font-family: Corbel">
      $al
    </p>
  </body>
</html></richcontent>
